mapper.select:
SELECT A.product_code, A.product_name, A.price, B.img_code, B.source_name, B.convert_path, A.reviews, B.image_classification, F.status, F.end_day, F.discount FROM tbl_product A LEFT JOIN tbl_product_img B ON (A.product_code = B.product_code) LEFT JOIN tbl_category C ON (A.category_code = C.category_code) LEFT JOIN tbl_event_product_list E ON (E.product_code = A.product_code) LEFT JOIN tbl_event F ON (F.post_code = E.post_code) WHERE A.orderable_status = 'Y' AND B.image_status = 'Y' AND C.state = 'Y' AND B.image_classification = 'Thumbnails' AND ( (#{type} = 'bed' AND c.name = '침대' AND A.event_progressionstatus = 'N') OR (#{type} = 'wardrobe' AND C.name = '옷장' AND A.event_progressionstatus = 'N') OR (#{type} = 'dressingTable' AND C.name = '화장대' AND A.event_progressionstatus = 'N') OR (#{type} = 'sopa' AND C.name = '소파' AND A.event_progressionstatus = 'N') OR (#{type} = 'table' AND C.name = '테이블' AND A.event_progressionstatus = 'N') OR (#{type} = 'closet' AND C.name = '수납장' AND A.event_progressionstatus = 'N') OR (#{type} = 'desk' AND C.name = '책상' AND A.event_progressionstatus = 'N') OR (#{type} = 'bookcase' AND C.name = '책장' AND A.event_progressionstatus = 'N') OR (#{type} = 'chair' AND C.name = '의자' AND A.event_progressionstatus = 'N') OR (#{type} = 'kitchenTable' AND C.name = '식탁' AND A.event_progressionstatus = 'N') OR (#{type} = 'kitchenCloset' AND C.name = '수납장' AND C.category_code = '15' AND A.event_progressionstatus = 'N')
OR (#{type} = 'sale' AND F.status = 'Y' AND A.event_progressionstatus = 'Y')
OR (#{type} = 'best' AND F.status = 'A' AND A.event_progressionstatus = 'A') ) ORDER BY A.product_code LIMIT #{selectCriteria.startRow}, #{ selectCriteria.limit }
